public class P1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i = 1; i <= 6; i++) {
			
			if(i == 1 || i == 6) {
				for(int j=1; j < 6; j++) {
					System.out.print("* ");
				}
				System.out.println();
			}
			
			else {
				
				for(int j = 1; j < 6; j++ ) {
					if(j == 2 || j == 3 || j == 4) {
						System.out.print("  ");
					}
					
					else {
						System.out.print("* ");
					}
				
				}
				
				System.out.println();
					
			} 
			} 
		} } 
